#include <stdint.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{

	printf("in thread mode before interrupt");

	change_access_lvl_to_unprivilaged();

	generate_interrupt();

    /* Loop forever */
	for(;;);
}


void change_access_lvl_to_unprivilaged() {
	// read
	__asm volatile ("MRS R0,CONTROL");
	// modify
	__asm volatile ("ORR R0, R0,#0x1");  // ORR == bitwise OR
	// write
	__asm volatile ("MSR CONTROL,R0");
}

void generate_interrupt() {
	unint32_t *pSTIR = (unint32_t*)0xE000EF00;
	unint32_t *pISER0 = (unint32_t*)0xE000E100;

	// enable IRQ3 interrupt
	*pISER0 |= (1 << 3);

	// generate an interrupt from software to IRQ3
	*pSTIR |= (3 & 0x1FF);


}
